import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  border-radius: 16px;
  padding: 2rem;
  width: 90%;
  max-width: 500px;
  max-height: 90vh;
  overflow-y: auto;
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1.5rem;
`;

const ModalTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #1e293b;
`;

const CloseButton = styled.button`
  padding: 0.5rem;
  background-color: #f3f4f6;
  color: #6b7280;
  border-radius: 8px;
  font-size: 1.2rem;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #e5e7eb;
  }
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const Label = styled.label`
  font-size: 0.9rem;
  font-weight: 500;
  color: #374151;

  &.required {
    color: #dc2626;
  }
`;

const RequiredStar = styled.span`
  color: #dc2626;
  margin-left: 2px;
`;

const ErrorMessage = styled.div`
  color: #dc2626;
  font-size: 0.8rem;
  margin-top: 0.25rem;
`;

const Input = styled.input`
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 8px;
  font-size: 0.9rem;
  transition: border-color 0.2s ease;

  &:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
  }

  &.error {
    border-color: #dc2626;
    box-shadow: 0 0 0 3px rgba(220, 38, 38, 0.1);
  }
`;

const TextArea = styled.textarea`
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 8px;
  font-size: 0.9rem;
  min-height: 80px;
  resize: vertical;
  transition: border-color 0.2s ease;

  &:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
  }

  &.error {
    border-color: #dc2626;
    box-shadow: 0 0 0 3px rgba(220, 38, 38, 0.1);
  }
`;


const AddressGroup = styled.div`
  display: flex;
  gap: 0.5rem;
  align-items: flex-end;
`;

const AddressInput = styled(Input)`
  &.zipcode {
    width: 120px;
  }

  &.address {
    flex: 1;
  }
`;

const AddressButton = styled.button`
  padding: 0.75rem 1rem;
  background-color: #3b82f6;
  color: white;
  border-radius: 8px;
  font-size: 0.9rem;
  font-weight: 500;
  white-space: nowrap;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #2563eb;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
`;

const Button = styled.button`
  flex: 1;
  padding: 0.75rem 1rem;
  border-radius: 8px;
  font-size: 0.9rem;
  font-weight: 500;
  transition: all 0.2s ease;

  &.primary {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;

    &:hover {
      transform: translateY(-1px);
    }
  }

  &.secondary {
    background-color: #f3f4f6;
    color: #374151;

    &:hover {
      background-color: #e5e7eb;
    }
  }
`;

const GroupModal = ({ group, onSave, onClose }) => {
  const [formData, setFormData] = useState({
    name: '',
    organizationName: '',
    managerName: '',
    zipcode: '',
    address: '',
    detailAddress: '',
    mobilePhone: '',
    phone: '',
    description: ''
  });

  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    if (group) {
      // 기존 주소를 분리 (예: "12345 서울시 강남구 테헤란로 123")
      const addressParts = group.address ? group.address.split(' ') : ['', '', ''];
      setFormData({
        name: group.name || '',
        organizationName: group.organizationName || '',
        managerName: group.managerName || '',
        zipcode: addressParts[0] || '',
        address: addressParts.slice(1).join(' ') || '',
        detailAddress: group.detailAddress || '',
        mobilePhone: group.mobilePhone || '',
        phone: group.phone || '',
        description: group.description || ''
      });
    } else {
      setFormData({
        name: '',
        organizationName: '',
        managerName: '',
        zipcode: '',
        address: '',
        detailAddress: '',
        mobilePhone: '',
        phone: '',
        description: ''
      });
    }
  }, [group]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddressSearch = () => {
    // 다음 우편번호 서비스 사용
    new window.daum.Postcode({
      oncomplete: function(data) {
        // 우편번호와 주소 정보를 해당 필드에 넣는다.
        setFormData(prev => ({
          ...prev,
          zipcode: data.zonecode,
          address: data.address
        }));
      }
    }).open();
  };

  const validateForm = () => {
    const newErrors = {};

    // 필수 필드 검증
    if (!formData.name.trim()) {
      newErrors.name = '그룹명을 입력해주세요.';
    } else if (formData.name.length > 15) {
      newErrors.name = '그룹명은 15자 이내로 입력해주세요.';
    }

    if (!formData.organizationName.trim()) {
      newErrors.organizationName = '기관명을 입력해주세요.';
    } else if (formData.organizationName.length > 25) {
      newErrors.organizationName = '기관명은 25자 이내로 입력해주세요.';
    }

    if (!formData.managerName.trim()) {
      newErrors.managerName = '담당자명을 입력해주세요.';
    } else if (formData.managerName.length > 20) {
      newErrors.managerName = '담당자명은 20자 이내로 입력해주세요.';
    }

    if (!formData.address.trim()) {
      newErrors.address = '주소를 입력해주세요.';
    }

    if (!formData.mobilePhone.trim()) {
      newErrors.mobilePhone = '핸드폰 번호를 입력해주세요.';
    }

    if (!formData.phone.trim()) {
      newErrors.phone = '일반전화 번호를 입력해주세요.';
    }

    if (!formData.description.trim()) {
      newErrors.description = '설명을 입력해주세요.';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (isSubmitting) return;

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      // 주소 정보를 합쳐서 저장
      const fullAddress = `${formData.zipcode} ${formData.address}`.trim();
      const submitData = {
        ...formData,
        address: fullAddress
      };

      await onSave(submitData);
    } catch (error) {
      console.error('저장 중 오류가 발생했습니다:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <ModalOverlay onClick={onClose}>
      <ModalContent onClick={(e) => e.stopPropagation()}>
        <ModalHeader>
          <ModalTitle>
            {group ? '그룹 수정' : '그룹 추가'}
          </ModalTitle>
          <CloseButton onClick={onClose}>×</CloseButton>
        </ModalHeader>

        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label htmlFor="name">그룹명 *</Label>
            <Input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="그룹명을 입력하세요"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="organizationName">단체명</Label>
            <Input
              type="text"
              id="organizationName"
              name="organizationName"
              value={formData.organizationName}
              onChange={handleChange}
              placeholder="단체명을 입력하세요"
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="managerName">담당자명</Label>
            <Input
              type="text"
              id="managerName"
              name="managerName"
              value={formData.managerName}
              onChange={handleChange}
              placeholder="담당자명을 입력하세요"
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="zipcode">주소</Label>
            <AddressGroup>
              <AddressInput
                type="text"
                id="zipcode"
                name="zipcode"
                className="zipcode"
                value={formData.zipcode}
                onChange={handleChange}
                placeholder="우편번호"
                readOnly
              />
              <AddressButton type="button" onClick={handleAddressSearch}>
                주소검색
              </AddressButton>
            </AddressGroup>
            <AddressInput
              type="text"
              name="address"
              className="address"
              value={formData.address}
              onChange={handleChange}
              placeholder="기본주소"
              readOnly
              style={{ marginTop: '0.5rem' }}
            />
            <Input
              type="text"
              name="detailAddress"
              value={formData.detailAddress}
              onChange={handleChange}
              placeholder="상세주소를 입력하세요"
              style={{ marginTop: '0.5rem' }}
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="mobilePhone">핸드폰</Label>
            <Input
              type="tel"
              id="mobilePhone"
              name="mobilePhone"
              value={formData.mobilePhone}
              onChange={handleChange}
              placeholder="010-1234-5678"
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="phone">일반전화</Label>
            <Input
              type="tel"
              id="phone"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              placeholder="02-1234-5678"
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="description">설명</Label>
            <TextArea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              placeholder="그룹에 대한 설명을 입력하세요"
            />
          </FormGroup>

          <ButtonGroup>
            <Button type="button" className="secondary" onClick={onClose}>
              취소
            </Button>
            <Button type="submit" className="primary">
              {group ? '수정' : '추가'}
            </Button>
          </ButtonGroup>
        </Form>
      </ModalContent>
    </ModalOverlay>
  );
};

export default GroupModal;
