import React, { useState } from 'react';
import styled from 'styled-components';
import { authAPI } from '../services/api';

const LoginContainer = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  padding: 2rem;
`;

const LoginCard = styled.div`
  background: white;
  border-radius: 20px;
  padding: 3rem;
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
  width: 100%;
  max-width: 400px;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  font-weight: 700;
  color: #1e293b;
  text-align: center;
  margin-bottom: 0.5rem;
`;

const Subtitle = styled.p`
  font-size: 1.1rem;
  color: #64748b;
  text-align: center;
  margin-bottom: 2.5rem;
`;

const FormGroup = styled.div`
  margin-bottom: 1.5rem;
`;

const Label = styled.label`
  display: block;
  font-size: 1rem;
  font-weight: 500;
  color: #374151;
  margin-bottom: 0.5rem;
`;

const Input = styled.input`
  width: 100%;
  padding: 1rem;
  border: 2px solid #e5e7eb;
  border-radius: 12px;
  font-size: 1rem;
  transition: border-color 0.2s ease;

  &:focus {
    border-color: #667eea;
  }

  &::placeholder {
    color: #9ca3af;
  }
`;

const LoginButton = styled.button`
  width: 100%;
  padding: 1rem;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border-radius: 12px;
  font-size: 1.1rem;
  font-weight: 600;
  transition: transform 0.2s ease, box-shadow 0.2s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 25px -5px rgba(102, 126, 234, 0.4);
  }

  &:active {
    transform: translateY(0);
  }
`;

const ErrorMessage = styled.div`
  color: #ef4444;
  font-size: 0.9rem;
  margin-top: 0.5rem;
  text-align: center;
`;

const SignupMessage = styled.div`
  margin-top: 1rem;
  text-align: center;
`;

const SignupText = styled.p`
  color: #64748b;
  font-size: 0.9rem;
  margin-bottom: 0.5rem;
`;

const SignupLink = styled.a`
  color: #667eea;
  text-decoration: none;
  font-size: 0.9rem;
  font-weight: 500;
  transition: color 0.2s ease;

  &:hover {
    color: #5a67d8;
    text-decoration: underline;
  }
`;

const LoginPage = ({ onLogin }) => {
  const [businessNumber, setBusinessNumber] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [fieldErrors, setFieldErrors] = useState({});

  const validateField = (name, value) => {
    let errorMessage = '';

    switch (name) {
      case 'businessNumber':
        if (!value.trim()) {
          errorMessage = '사업자번호를 입력해주세요.';
        }
        break;
      case 'password':
        if (!value.trim()) {
          errorMessage = '비밀번호를 입력해주세요.';
        }
        break;
    }

    setFieldErrors(prev => ({
      ...prev,
      [name]: errorMessage
    }));
  };

  const handleInputChange = (name, value) => {
    if (name === 'businessNumber') {
      setBusinessNumber(value);
    } else if (name === 'password') {
      setPassword(value);
    }
    
    // 실시간 검증
    validateField(name, value);
    
    // 전체 에러 메시지 초기화
    if (error) {
      setError('');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // 빈 값 검증
    if (!businessNumber.trim() && !password.trim()) {
      setError('사업자번호와 비밀번호를 모두 입력해주세요.');
      return;
    }
    
    if (!businessNumber.trim()) {
      setError('사업자번호를 입력해주세요.');
      return;
    }
    
    if (!password.trim()) {
      setError('비밀번호를 입력해주세요.');
      return;
    }

    try {
      // 실제 Lambda API 호출
      const response = await authAPI.login(businessNumber, password);
      
      if (response.ok) {
        onLogin({
          businessNumber,
          businessName: response.user.orgName,
          businessOwner: response.user.orgName
        });
      } else {
        setError(response.message || '사업자번호 또는 비밀번호가 올바르지 않습니다.');
      }
    } catch (err) {
      setError(err.message || '로그인 중 오류가 발생했습니다.');
    }
  };

  return (
    <LoginContainer>
      <LoginCard>
        <Title>나눔번들</Title>
        <Subtitle>사업자 관리 시스템</Subtitle>
        
        <form onSubmit={handleSubmit}>
          <FormGroup>
            <Label htmlFor="businessNumber">사업자번호</Label>
            <Input
              id="businessNumber"
              type="text"
              placeholder="사업자번호를 입력하세요"
              value={businessNumber}
              onChange={(e) => setBusinessNumber(e.target.value)}
            />
          </FormGroup>
          
          <FormGroup>
            <Label htmlFor="password">비밀번호</Label>
            <Input
              id="password"
              type="password"
              placeholder="비밀번호를 입력하세요"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </FormGroup>
          
          {error && <ErrorMessage>{error}</ErrorMessage>}
          
          <LoginButton type="submit">
            로그인
          </LoginButton>

          {error && error !== '사업자번호와 비밀번호를 입력해주세요.' && (
            <SignupMessage>
              <SignupText>아름다운가게 나눔사업 사이트에 먼저 가입 후 이용해 주세요</SignupText>
              <SignupLink href="https://nanum.beautifulstore.org" target="_blank" rel="noopener noreferrer">
                나눔사업 사이트 바로가기 →
              </SignupLink>
            </SignupMessage>
          )}
        </form>
      </LoginCard>
    </LoginContainer>
  );
};

export default LoginPage;
