const TargetModal = ({ target, onSave, onClose }) => {
  const [formData, setFormData] = useState({
    name: '',
    targetType: '',
    targetHousehold: '',
    zipcode: '',
    address: '',
    detailAddress: '',
    mobilePhone: '',
    phone: '',
    applicationReason: '',
    directions: ''
  });

  useEffect(() => {
    if (target) {
      // 기존 주소를 분리 (예: "12345 서울시 강남구 테헤란로 123")
      const addressParts = target.address ? target.address.split(' ') : ['', '', ''];
      setFormData({
        name: target.name || '',
        targetType: target.targetType || '',
        targetHousehold: target.targetHousehold || '',
        zipcode: addressParts[0] || '',
        address: addressParts.slice(1).join(' ') || '',
        detailAddress: target.detailAddress || '',
        mobilePhone: target.mobilePhone || '',
        homePhone: target.homePhone || '',
        applicationReason: target.applicationReason || '',
        directions: target.directions || ''
      });
    } else {
      setFormData({
        name: '',
        targetType: '',
        targetHousehold: '',
        zipcode: '',
        address: '',
        detailAddress: '',
        mobilePhone: '',
        homePhone: '',
        applicationReason: '',
        directions: ''
      });
    }
  }, [target]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddressSearch = () => {
    // 다음 주소검색 API 스크립트 로드
    if (!window.daum) {
      const script = document.createElement('script');
      script.src = '//t1.daumcdn.net/mapjsapi/bundle/postcode/prod/postcode.v2.js';
      script.onload = () => {
        openAddressSearch();
      };
      document.head.appendChild(script);
    } else {
      openAddressSearch();
    }
  };

  const openAddressSearch = () => {
    new window.daum.Postcode({
      oncomplete: function(data) {
        // 우편번호와 주소 정보를 해당 필드에 넣는다.
        setFormData(prev => ({
          ...prev,
          zipcode: data.zonecode,
          address: data.address
        }));
      }
    }).open();
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // 필수 입력 항목 검증
    if (!formData.name.trim()) {
      alert('대상자명은 필수 입력 항목입니다.');
      return;
    }

    if (!formData.targetType.trim()) {
      alert('대상구분은 필수 입력 항목입니다.');
      return;
    }

    if (!formData.targetHousehold.trim()) {
      alert('대상가구는 필수 입력 항목입니다.');
      return;
    }

    if (!formData.zipcode.trim() || !formData.address.trim()) {
      alert('주소는 필수 입력 항목입니다.');
      return;
    }

    if (!formData.mobilePhone.trim()) {
      alert('핸드폰은 필수 입력 항목입니다.');
      return;
    }

    if (!formData.applicationReason.trim()) {
      alert('신청사유는 필수 입력 항목입니다.');
      return;
    }

    // 주소 정보를 합쳐서 저장
    const fullAddress = `${formData.zipcode} ${formData.address}`.trim();
    const submitData = {
      ...formData,
      address: fullAddress
    };

    onSave(submitData);
  };

  return (
    <ModalOverlay onClick={onClose}>
      <ModalContent onClick={(e) => e.stopPropagation()}>
        <ModalHeader>
          <ModalTitle>
            {target ? '대상자 수정' : '대상자 추가'}
          </ModalTitle>
          <CloseButton onClick={onClose}>×</CloseButton>
        </ModalHeader>

        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label htmlFor="name">대상자명 *</Label>
            <Input
              id="name"
              name="name"
              type="text"
              value={formData.name}
              onChange={handleChange}
              placeholder="대상자명을 입력하세요"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="targetType">대상구분 *</Label>
            <Select
              id="targetType"
              name="targetType"
              value={formData.targetType}
              onChange={handleChange}
              required
            >
              <option value="">선택하세요</option>
              <option value="수급자">수급자</option>
              <option value="차상위">차상위</option>
              <option value="일반저소득">일반저소득</option>
            </Select>
          </FormGroup>

          <FormGroup>
            <Label htmlFor="targetHousehold">대상가구 *</Label>
            <Select
              id="targetHousehold"
              name="targetHousehold"
              value={formData.targetHousehold}
              onChange={handleChange}
              required
            >
              <option value="">선택하세요</option>
              <option value="독거어르신">독거어르신</option>
              <option value="조손가구">조손가구</option>
              <option value="저소득1인가구">저소득1인가구</option>
              <option value="겨울철에너지취약계층">겨울철에너지취약계층</option>
            </Select>
          </FormGroup>

          <FormGroup>
            <Label htmlFor="zipcode">주소 *</Label>
            <AddressGroup>
              <AddressInput
                id="zipcode"
                name="zipcode"
                type="text"
                className="zipcode"
                value={formData.zipcode}
                onChange={handleChange}
                placeholder="우편번호"
                readOnly
              />
              <AddressButton type="button" onClick={handleAddressSearch}>
                주소검색
              </AddressButton>
            </AddressGroup>
            <AddressInput
              name="address"
              type="text"
              className="address"
              value={formData.address}
              onChange={handleChange}
              placeholder="기본주소"
              readOnly
              style={{ marginTop: '0.5rem' }}
            />
            <Input
              name="detailAddress"
              type="text"
              value={formData.detailAddress}
              onChange={handleChange}
              placeholder="상세주소를 입력하세요"
              style={{ marginTop: '0.5rem' }}
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="mobilePhone">핸드폰 *</Label>
            <Input
              id="mobilePhone"
              name="mobilePhone"
              type="tel"
              value={formData.mobilePhone}
              onChange={handleChange}
              placeholder="010-1234-5678"
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="homePhone">집전화</Label>
            <Input
              id="homePhone"
              name="homePhone"
              type="tel"
              value={formData.homePhone}
              onChange={handleChange}
              placeholder="02-1234-5678"
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="applicationReason">신청사유 (1000자까지) *</Label>
            <TextArea
              id="applicationReason"
              name="applicationReason"
              value={formData.applicationReason}
              onChange={handleChange}
              placeholder="신청사유를 입력하세요"
              maxLength="1000"
              style={{ minHeight: '100px' }}
            />
            <div style={{ fontSize: '0.8rem', color: '#6b7280', textAlign: 'right', marginTop: '0.25rem' }}>
              {formData.applicationReason.length}/1000
            </div>
          </FormGroup>

          <FormGroup>
            <Label htmlFor="directions">찾아가는길 (100자까지)</Label>
            <TextArea
              id="directions"
              name="directions"
              value={formData.directions}
              onChange={handleChange}
              placeholder="찾아가는길을 입력하세요"
              maxLength="100"
              style={{ minHeight: '60px' }}
            />
            <div style={{ fontSize: '0.8rem', color: '#6b7280', textAlign: 'right', marginTop: '0.25rem' }}>
              {formData.directions.length}/100
            </div>
          </FormGroup>

          <ButtonGroup>
            <Button type="button" className="cancel" onClick={onClose}>
              취소
            </Button>
            <Button type="submit" className="save">
              {target ? '수정' : '추가'}
            </Button>
          </ButtonGroup>
        </Form>
      </ModalContent>
    </ModalOverlay>
  );
};

export default TargetModal;
