import React, { useState } from 'react';
import styled from 'styled-components';
import TargetModal from './TargetModal';
import ConfirmModal from './ConfirmModal';
import * as XLSX from 'xlsx';
import { targetAPI } from '../services/api';

const TargetListContainer = styled.div`
  height: 100%;
  display: flex;
  flex-direction: column;
`;

const Header = styled.div`
  padding: 1.5rem;
  border-bottom: 1px solid #e5e7eb;
  background: white;
`;

const HeaderTop = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
`;

const Title = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #1e293b;
`;

const CloseButton = styled.button`
  padding: 0.5rem;
  background-color: #f3f4f6;
  color: #6b7280;
  border-radius: 8px;
  font-size: 1.2rem;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #e5e7eb;
  }
`;

const GroupInfo = styled.div`
  color: #64748b;
  font-size: 0.95rem;
`;

const ActionBar = styled.div`
  padding: 1rem 1.5rem;
  border-bottom: 1px solid #e5e7eb;
  background: #f8fafc;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const SearchInput = styled.input`
  padding: 0.75rem 1rem;
  border: 1px solid #d1d5db;
  border-radius: 8px;
  font-size: 0.9rem;
  width: 250px;

  &:focus {
    border-color: #667eea;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 0.75rem;
`;

const AddButton = styled.button`
  padding: 0.75rem 1.5rem;
  background: linear-gradient(135deg, #10b981 0%, #059669 100%);
  color: white;
  border-radius: 8px;
  font-size: 0.9rem;
  font-weight: 500;
  transition: transform 0.2s ease;

  &:hover {
    transform: translateY(-1px);
  }
`;

const ExcelButton = styled.button`
  padding: 0.75rem 1.5rem;
  background: linear-gradient(135deg, #059669 0%, #047857 100%);
  color: white;
  border-radius: 8px;
  font-size: 0.9rem;
  font-weight: 500;
  transition: transform 0.2s ease;
  white-space: nowrap;

  &:hover {
    transform: translateY(-1px);
  }
`;

const ListContainer = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 1rem;
`;

const TargetItem = styled.div`
  background: white;
  border: 1px solid #e5e7eb;
  border-radius: 12px;
  padding: 1rem;
  margin-bottom: 0.75rem;
  transition: all 0.2s ease;

  &:hover {
    border-color: #667eea;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
  }
`;

const TargetHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 0.5rem;
`;

const TargetName = styled.h4`
  font-size: 1.1rem;
  font-weight: 600;
  color: #1e293b;
`;

const TargetStatus = styled.span`
  padding: 0.25rem 0.5rem;
  border-radius: 12px;
  font-size: 0.75rem;
  font-weight: 500;
  background-color: ${props => props.status === 'active' ? '#dcfce7' : '#fef3c7'};
  color: ${props => props.status === 'active' ? '#166534' : '#92400e'};
`;

const TargetDetails = styled.div`
  color: #64748b;
  font-size: 0.9rem;
  margin-bottom: 0.75rem;
`;

const TargetActions = styled.div`
  display: flex;
  gap: 0.5rem;
`;

const ActionButton = styled.button`
  padding: 0.5rem 1rem;
  border-radius: 6px;
  font-size: 0.8rem;
  font-weight: 500;
  transition: all 0.2s ease;

  &.edit {
    background-color: #3b82f6;
    color: white;

    &:hover {
      background-color: #2563eb;
    }
  }

  &.delete {
    background-color: #ef4444;
    color: white;

    &:hover {
      background-color: #dc2626;
    }
  }
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 3rem;
  color: #64748b;
`;

const EmptyIcon = styled.div`
  font-size: 2.5rem;
  margin-bottom: 1rem;
`;

const EmptyText = styled.p`
  font-size: 1rem;
`;

const TargetList = ({ group, targets, onClose, onTargetUpdate }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedTarget, setSelectedTarget] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [targetToDelete, setTargetToDelete] = useState(null);

  const filteredTargets = targets.filter(target =>
    (target.name && target.name.toLowerCase().includes(searchTerm.toLowerCase())) ||
    (target.mobilePhone && target.mobilePhone.includes(searchTerm)) ||
    (target.phone && target.phone.includes(searchTerm)) ||
    (target.targetType && target.targetType.includes(searchTerm)) ||
    (target.targetHousehold && target.targetHousehold.includes(searchTerm))
  );

  const handleEdit = (target) => {
    setSelectedTarget(target);
    setIsModalOpen(true);
  };

  const handleDelete = (target) => {
    setTargetToDelete(target);
    setIsDeleteModalOpen(true);
  };

  const handleAdd = () => {
    setSelectedTarget(null);
    setIsModalOpen(true);
  };

  const handleSave = async (targetData) => {
    try {
      if (selectedTarget) {
        // 수정
        await targetAPI.updateTarget(group.group_id, selectedTarget.id, targetData);

        // 목록 새로고침
        const response = await targetAPI.getTargets(group.group_id);
        if (response.ok && response.data) {
          onTargetUpdate(response.data);
        }
      } else {
        // 추가
        const response = await targetAPI.createTarget(group.group_id, targetData);
        if (response.ok) {
          // 목록 새로고침
          const targetsResponse = await targetAPI.getTargets(group.group_id);
          if (targetsResponse.ok && targetsResponse.data) {
            onTargetUpdate(targetsResponse.data);
          }
        }
      }

      setIsModalOpen(false);
      setSelectedTarget(null);
    } catch (error) {
      console.error('대상자 저장 실패:', error);
      alert('저장에 실패했습니다. 다시 시도해주세요.');
    }
  };

  const handleConfirmDelete = async () => {
    try {
      await targetAPI.deleteTarget(group.group_id, targetToDelete.id);

      // 목록 새로고침
      const response = await targetAPI.getTargets(group.group_id);
      if (response.ok && response.data) {
        onTargetUpdate(response.data);
      }

      setIsDeleteModalOpen(false);
      setTargetToDelete(null);
    } catch (error) {
      console.error('대상자 삭제 실패:', error);
      alert('삭제에 실패했습니다. 다시 시도해주세요.');
    }
  };

  const handleExcelDownload = () => {
    // 엑셀 워크북 생성
    const wb = XLSX.utils.book_new();

    // 워크시트 데이터 생성
    const wsData = [];

    // 1. 단체정보 섹션
    wsData.push(['1.단체정보']);
    wsData.push(['단체명', '']);
    wsData.push(['주소', '']);
    wsData.push(['담당자명', '']);
    wsData.push(['핸드폰', '']);
    wsData.push(['일반전화', '']);
    wsData.push([]);

    // 2. 추천대상 정보 섹션
    wsData.push(['2.추천대상정보']);
    wsData.push(['연번', '대상자명', '대상구분', '대상가구', '우편번호', '기본주소', '상세주소', '핸드폰', '집전화', '신청사유', '찾아가는길']);

    // 텍스트를 30자씩 강제 줄바꿈하는 함수
    const wrapText = (text, maxLength = 30) => {
      if (!text) return '';
      const lines = [];
      for (let i = 0; i < text.length; i += maxLength) {
        lines.push(text.substring(i, i + maxLength));
      }
      return lines.join('\n');
    };

    // 대상자 데이터 추가
    targets.forEach((target, index) => {
      const addressParts = target.address ? target.address.split(' ') : ['', ''];
      const zipcode = addressParts[0] || '';
      const roadAddress = addressParts.slice(1).join(' ') || '';

      wsData.push([
        index + 1,
        target.name || '',
        target.targetType || '',
        target.targetHousehold || '',
        zipcode,
        roadAddress,
        target.detailAddress || '',
        target.mobilePhone || '',
        target.phone || '',
        wrapText(target.applicationReason || '', 30),
        wrapText(target.directions || '', 30)
      ]);
    });

    // 그룹 정보를 워크시트에 추가
    if (group) {
      // 단체정보 섹션에 그룹 정보 입력
      wsData[1][1] = group.organizationName || '';  // 단체명
      wsData[2][1] = group.address || '';          // 주소
      wsData[3][1] = group.managerName || '';      // 담당자명
      wsData[4][1] = group.mobilePhone || '';      // 핸드폰
      wsData[5][1] = group.phone || '';            // 일반전화
    }

    // 워크시트 생성
    const ws = XLSX.utils.aoa_to_sheet(wsData);

    // 셀 스타일 설정 (텍스트 줄바꿈)
    const range = XLSX.utils.decode_range(ws['!ref']);
    for (let row = range.s.r; row <= range.e.r; row++) {
      for (let col = range.s.c; col <= range.e.c; col++) {
        const cellAddress = XLSX.utils.encode_cell({ r: row, c: col });
        if (!ws[cellAddress]) continue;

        // 신청사유(9번 컬럼)와 찾아가는길(10번 컬럼)에 텍스트 줄바꿈 적용
        if (col === 9 || col === 10) {
          ws[cellAddress].s = {
            alignment: {
              wrapText: true,
              vertical: 'top'
            }
          };
        }
      }
    }

    // 셀 병합 설정 (새로운 구조에 맞게 수정)
    const merges = [];

    ws['!merges'] = merges;

    // 열 너비 설정
    ws['!cols'] = [
      { wch: 8 },  // 연번
      { wch: 15 }, // 대상자명
      { wch: 15 }, // 대상구분
      { wch: 20 }, // 대상가구
      { wch: 12 }, // 우편번호
      { wch: 25 }, // 기본주소
      { wch: 20 }, // 상세주소
      { wch: 15 }, // 핸드폰
      { wch: 15 }, // 집전화
      { wch: 50 }, // 신청사유 (넓게)
      { wch: 30 }  // 찾아가는길
    ];

    // 행 높이 설정 (특히 신청사유, 찾아가는길 행을 높게)
    ws['!rows'] = [];
    for (let i = 0; i < wsData.length; i++) {
      if (i >= 7) { // 대상자 데이터 행들 (헤더 포함)
        // 신청사유와 찾아가는길 컬럼의 행 높이를 높게 설정
        ws['!rows'][i] = { hpt: 100 }; // 여러 줄 텍스트를 위해 더 높게
      } else {
        ws['!rows'][i] = { hpt: 20 }; // 기본 높이
      }
    }

    // 워크시트를 워크북에 추가
    XLSX.utils.book_append_sheet(wb, ws, '대상자명단');

    // 엑셀 파일 다운로드
    const fileName = `${group?.name || '대상자'}_명단_${new Date().toISOString().split('T')[0]}.xlsx`;
    XLSX.writeFile(wb, fileName);
  };

  return (
    <TargetListContainer>
      <Header>
        <HeaderTop>
          <Title>대상자 관리</Title>
          <CloseButton onClick={onClose}>×</CloseButton>
        </HeaderTop>
        <GroupInfo>
          {group.name} - 총 {targets.length}명
        </GroupInfo>
      </Header>

      <ActionBar>
        <SearchInput
          type="text"
          placeholder="대상자명, 전화번호, 대상구분, 대상가구로 검색..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <ButtonGroup>
          <ExcelButton onClick={handleExcelDownload}>
            📊 엑셀받기
          </ExcelButton>
          <AddButton onClick={handleAdd}>
            + 대상자 추가
          </AddButton>
        </ButtonGroup>
      </ActionBar>

      <ListContainer>
        {filteredTargets.length === 0 ? (
          <EmptyState>
            <EmptyIcon>👥</EmptyIcon>
            <EmptyText>
              {searchTerm ? '검색 결과가 없습니다.' : '등록된 대상자가 없습니다.'}
            </EmptyText>
          </EmptyState>
        ) : (
          filteredTargets.map((target) => (
            <TargetItem key={target.id}>
              <TargetHeader>
                <TargetName>{target.name}</TargetName>
                <TargetStatus status={target.status}>
                  {target.status === 'active' ? '활성' : '비활성'}
                </TargetStatus>
              </TargetHeader>

              <TargetDetails>
                {target.targetType && <div>🏷️ 대상구분: {target.targetType}</div>}
                {target.targetHousehold && <div>🏠 대상가구: {target.targetHousehold}</div>}
                {target.mobilePhone && <div>📱 핸드폰: {target.mobilePhone}</div>}
                {target.homePhone && <div>☎️ 집전화: {target.homePhone}</div>}
                <div>📍 {target.address}</div>
                {target.detailAddress && <div>📍 상세: {target.detailAddress}</div>}
                {target.applicationReason && <div>📝 신청사유: {target.applicationReason.substring(0, 50)}{target.applicationReason.length > 50 ? '...' : ''}</div>}
                {target.directions && <div>🗺️ 찾아가는길: {target.directions}</div>}
                <div>📅 등록일: {target.registeredAt}</div>
              </TargetDetails>

              <TargetActions>
                <ActionButton 
                  className="edit"
                  onClick={() => handleEdit(target)}
                >
                  수정
                </ActionButton>
                <ActionButton 
                  className="delete"
                  onClick={() => handleDelete(target)}
                >
                  삭제
                </ActionButton>
              </TargetActions>
            </TargetItem>
          ))
        )}
      </ListContainer>

      {isModalOpen && (
        <TargetModal
          target={selectedTarget}
          onSave={handleSave}
          onClose={() => {
            setIsModalOpen(false);
            setSelectedTarget(null);
          }}
        />
      )}

      {isDeleteModalOpen && (
        <ConfirmModal
          title="대상자 삭제"
          message={`${targetToDelete?.name}님을 삭제하시겠습니까?`}
          onConfirm={handleConfirmDelete}
          onCancel={() => {
            setIsDeleteModalOpen(false);
            setTargetToDelete(null);
          }}
        />
      )}
    </TargetListContainer>
  );
};

export default TargetList;
