import React, { useState } from 'react';
import styled from 'styled-components';
import TargetModal from './TargetModal';
import ConfirmModal from './ConfirmModal';
import * as XLSX from 'xlsx';
import { targetAPI } from '../services/api';

const TargetListContainer = styled.div`
  height: 100%;
  display: flex;
  flex-direction: column;
`;

const Header = styled.div`
  padding: 1.5rem;
  border-bottom: 1px solid #e5e7eb;
  background: white;
`;

const HeaderTop = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
`;

const Title = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #1e293b;
`;

const CloseButton = styled.button`
  padding: 0.5rem;
  background-color: #f3f4f6;
  color: #6b7280;
  border-radius: 8px;
  font-size: 1.2rem;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #e5e7eb;
  }
`;

const GroupInfo = styled.div`
  color: #64748b;
  font-size: 0.95rem;
`;

const ActionBar = styled.div`
  padding: 1rem 1.5rem;
  border-bottom: 1px solid #e5e7eb;
  background: #f8fafc;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const SearchInput = styled.input`
  padding: 0.75rem 1rem;
  border: 1px solid #d1d5db;
  border-radius: 8px;
  font-size: 0.9rem;
  width: 250px;

  &:focus {
    border-color: #85a65c;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 0.75rem;
`;

const AddButton = styled.button`
  padding: 0.75rem 1.5rem;
  background: linear-gradient(135deg, #10b981 0%, #059669 100%);
  color: white;
  border-radius: 8px;
  font-size: 0.9rem;
  font-weight: 500;
  transition: transform 0.2s ease;

  &:hover {
    transform: translateY(-1px);
  }
`;

const ExcelButton = styled.button`
  padding: 0.75rem 1.5rem;
  background: linear-gradient(135deg, #059669 0%, #047857 100%);
  color: white;
  border-radius: 8px;
  font-size: 0.9rem;
  font-weight: 500;
  transition: transform 0.2s ease;
  white-space: nowrap;

  &:hover {
    transform: translateY(-1px);
  }
`;

const ListContainer = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 1rem;
`;

const TargetItem = styled.div`
  background: white;
  border: 1px solid #e5e7eb;
  border-radius: 12px;
  padding: 1rem;
  margin-bottom: 0.75rem;
  transition: all 0.2s ease;

  &:hover {
    border-color: #667eea;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
  }
`;

const TargetHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 0.5rem;
`;

const TargetName = styled.h4`
  font-size: 1.1rem;
  font-weight: 600;
  color: #1e293b;
`;

const TargetStatus = styled.span`
  padding: 0.25rem 0.5rem;
  border-radius: 12px;
  font-size: 0.75rem;
  font-weight: 500;
  background-color: ${props => props.status === 'active' ? '#dcfce7' : '#fef3c7'};
  color: ${props => props.status === 'active' ? '#166534' : '#92400e'};
`;

const TargetDetails = styled.div`
  color: #64748b;
  font-size: 0.9rem;
  margin-bottom: 0.75rem;
`;

const TargetActions = styled.div`
  display: flex;
  gap: 0.5rem;
`;

const ActionButton = styled.button`
  padding: 0.5rem 1rem;
  border-radius: 6px;
  font-size: 0.8rem;
  font-weight: 500;
  transition: all 0.2s ease;

  &.edit {
    background-color: #3b82f6;
    color: white;

    &:hover {
      background-color: #2563eb;
    }
  }

  &.delete {
    background-color: #ef4444;
    color: white;

    &:hover {
      background-color: #dc2626;
    }
  }
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 3rem;
  color: #64748b;
`;

const EmptyIcon = styled.div`
  font-size: 2.5rem;
  margin-bottom: 1rem;
`;

const EmptyText = styled.p`
  font-size: 1rem;
`;

const TargetList = ({ group, targets, onClose, onTargetUpdate }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedTarget, setSelectedTarget] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [targetToDelete, setTargetToDelete] = useState(null);

  // Ï†ÑÌôîÎ≤àÌò∏ Ìè¨Îß∑ÌåÖ Ìï®Ïàò
  const formatPhoneNumber = (phone) => {
    if (!phone) return '';

    // Ïà´ÏûêÎßå Ï∂îÏ∂ú
    const numbers = phone.replace(/\D/g, '');

    // Ìï∏ÎìúÌè∞ Î≤àÌò∏ (010, 011, 016, 017, 018, 019) - 11ÏûêÎ¶¨
    if (numbers.length === 11 && (numbers.startsWith('010') || numbers.startsWith('011') || 
                                 numbers.startsWith('016') || numbers.startsWith('017') || 
                                 numbers.startsWith('018') || numbers.startsWith('019'))) {
      return `${numbers.slice(0, 3)}-${numbers.slice(3, 7)}-${numbers.slice(7)}`;
    }

    // ÏÑúÏö∏ ÏßÄÏó≠Î≤àÌò∏ (02) - 9ÏûêÎ¶¨ ÎòêÎäî 10ÏûêÎ¶¨
    if (numbers.startsWith('02')) {
      if (numbers.length === 9) {
        return `${numbers.slice(0, 2)}-${numbers.slice(2, 5)}-${numbers.slice(5)}`;
      } else if (numbers.length === 10) {
        return `${numbers.slice(0, 2)}-${numbers.slice(2, 6)}-${numbers.slice(6)}`;
      }
    }

    // Í∏∞ÌÉÄ ÏßÄÏó≠Î≤àÌò∏ (031, 032, 033, 041, 042, 043, 051, 052, 053, 061, 062, 063) - 10ÏûêÎ¶¨
    if (numbers.length === 10 && (numbers.startsWith('031') || numbers.startsWith('032') || numbers.startsWith('033') ||
                                 numbers.startsWith('041') || numbers.startsWith('042') || numbers.startsWith('043') ||
                                 numbers.startsWith('051') || numbers.startsWith('052') || numbers.startsWith('053') ||
                                 numbers.startsWith('061') || numbers.startsWith('062') || numbers.startsWith('063'))) {
      return `${numbers.slice(0, 3)}-${numbers.slice(3, 6)}-${numbers.slice(6)}`;
    }

    // Í∏∞ÌÉÄ 10ÏûêÎ¶¨ Î≤àÌò∏
    if (numbers.length === 10) {
      return `${numbers.slice(0, 3)}-${numbers.slice(3, 6)}-${numbers.slice(6)}`;
    }

    // 11ÏûêÎ¶¨ Ïù¥ÏÉÅ Î≤àÌò∏
    if (numbers.length >= 11) {
      return `${numbers.slice(0, 3)}-${numbers.slice(3, 7)}-${numbers.slice(7, 11)}`;
    }

    return phone; // Ìè¨Îß∑ÌåÖÌï† Ïàò ÏóÜÎäî Í≤ΩÏö∞ ÏõêÎ≥∏ Î∞òÌôò
  };

  const filteredTargets = targets.filter(target =>
    (target.name && target.name.toLowerCase().includes(searchTerm.toLowerCase())) ||
    (target.mobilePhone && target.mobilePhone.includes(searchTerm)) ||
    (target.phone && target.phone.includes(searchTerm)) ||
    (target.targetType && target.targetType.includes(searchTerm)) ||
    (target.targetHousehold && target.targetHousehold.includes(searchTerm))
  );

  const handleEdit = (target) => {
    setSelectedTarget(target);
    setIsModalOpen(true);
  };

  const handleDelete = (target) => {
    setTargetToDelete(target);
    setIsDeleteModalOpen(true);
  };

  const handleAdd = () => {
    setSelectedTarget(null);
    setIsModalOpen(true);
  };

  const handleSave = async (targetData) => {
    try {
      if (selectedTarget) {
        // ÏàòÏ†ï
        await targetAPI.updateTarget(group.group_id, selectedTarget.id, targetData);

        // Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
        const response = await targetAPI.getTargets(group.group_id);
        if (response.ok && response.data) {
          onTargetUpdate(response.data);
        }
      } else {
        // Ï∂îÍ∞Ä
        const response = await targetAPI.createTarget(group.group_id, targetData);
        if (response.ok) {
          // Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
          const targetsResponse = await targetAPI.getTargets(group.group_id);
          if (targetsResponse.ok && targetsResponse.data) {
            onTargetUpdate(targetsResponse.data);
          }
        }
      }

      setIsModalOpen(false);
      setSelectedTarget(null);
    } catch (error) {
      console.error('ÎåÄÏÉÅÏûê Ï†ÄÏû• Ïã§Ìå®:', error);
      alert('Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
    }
  };

  const handleConfirmDelete = async () => {
    try {
      await targetAPI.deleteTarget(group.group_id, targetToDelete.id);

      // Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
      const response = await targetAPI.getTargets(group.group_id);
      if (response.ok && response.data) {
        onTargetUpdate(response.data);
      }

      setIsDeleteModalOpen(false);
      setTargetToDelete(null);
    } catch (error) {
      console.error('ÎåÄÏÉÅÏûê ÏÇ≠Ï†ú Ïã§Ìå®:', error);
      alert('ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
    }
  };

  const handleExcelDownload = () => {
    // ÏóëÏÖÄ ÏõåÌÅ¨Î∂Å ÏÉùÏÑ±
    const wb = XLSX.utils.book_new();

    // ÏõåÌÅ¨ÏãúÌä∏ Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ±
    const wsData = [];

    // 1. Îã®Ï≤¥Ï†ïÎ≥¥ ÏÑπÏÖò
    wsData.push(['1.Îã®Ï≤¥Ï†ïÎ≥¥']);
    wsData.push(['Îã®Ï≤¥Î™Ö', '']);
    wsData.push(['Ï£ºÏÜå', '']);
    wsData.push(['Îã¥ÎãπÏûêÎ™Ö', '']);
    wsData.push(['Ìï∏ÎìúÌè∞', '']);
    wsData.push(['ÏùºÎ∞òÏ†ÑÌôî', '']);
    wsData.push([]);

    // 2. Ï∂îÏ≤úÎåÄÏÉÅ Ï†ïÎ≥¥ ÏÑπÏÖò
    wsData.push(['2.Ï∂îÏ≤úÎåÄÏÉÅÏ†ïÎ≥¥']);
    wsData.push(['Ïó∞Î≤à', 'ÎåÄÏÉÅÏûêÎ™Ö', 'ÎåÄÏÉÅÍµ¨Î∂Ñ', 'ÎåÄÏÉÅÍ∞ÄÍµ¨', 'Ïö∞Ìé∏Î≤àÌò∏', 'Í∏∞Î≥∏Ï£ºÏÜå', 'ÏÉÅÏÑ∏Ï£ºÏÜå', 'Ìï∏ÎìúÌè∞', 'ÏßëÏ†ÑÌôî', 'Ïã†Ï≤≠ÏÇ¨Ïú†', 'Ï∞æÏïÑÍ∞ÄÎäîÍ∏∏']);

    // wrapText Ìï®Ïàò Ï†úÍ±∞ - Í∏∞Î≥∏ ÌÖçÏä§Ìä∏ ÏÇ¨Ïö©

    // ÎåÄÏÉÅÏûê Îç∞Ïù¥ÌÑ∞ Ï∂îÍ∞Ä
    targets.forEach((target, index) => {
      const addressParts = target.address ? target.address.split(' ') : ['', ''];
      const zipcode = addressParts[0] || '';
      const roadAddress = addressParts.slice(1).join(' ') || '';

      wsData.push([
        index + 1,
        target.name || '',
        target.targetType || '',
        target.targetHousehold || '',
        zipcode,
        roadAddress,
        target.detailAddress || '',
        target.mobilePhone || '',
        target.phone || '',
        target.applicationReason || '',
        target.directions || ''
      ]);
    });

    // Í∑∏Î£π Ï†ïÎ≥¥Î•º ÏõåÌÅ¨ÏãúÌä∏Ïóê Ï∂îÍ∞Ä
    if (group) {
      // Îã®Ï≤¥Ï†ïÎ≥¥ ÏÑπÏÖòÏóê Í∑∏Î£π Ï†ïÎ≥¥ ÏûÖÎ†•
      wsData[1][1] = group.group_name || '';  // Îã®Ï≤¥Î™Ö
      wsData[2][1] = `${group.zipcode || ''} ${group.address1 || ''} ${group.address2 || ''}`.trim();  // Ï£ºÏÜå
      wsData[3][1] = group.contact_name || '';      // Îã¥ÎãπÏûêÎ™Ö
      wsData[4][1] = formatPhoneNumber(group.mobile_phone || '');      // Ìï∏ÎìúÌè∞
      wsData[5][1] = formatPhoneNumber(group.office_phone || '');      // ÏùºÎ∞òÏ†ÑÌôî
    }

    // ÏõåÌÅ¨ÏãúÌä∏ ÏÉùÏÑ±
    const ws = XLSX.utils.aoa_to_sheet(wsData);

    // ÏÖÄ Ïä§ÌÉÄÏùº ÏÑ§Ï†ï Ï†úÍ±∞ - Í∏∞Î≥∏ ÌñâÎÜíÏù¥ ÏÇ¨Ïö©

    // ÏÖÄ Î≥ëÌï© ÏÑ§Ï†ï (ÏÉàÎ°úÏö¥ Íµ¨Ï°∞Ïóê ÎßûÍ≤å ÏàòÏ†ï)
    const merges = [];

    ws['!merges'] = merges;

    // Ïó¥ ÎÑàÎπÑ ÏÑ§Ï†ï
    ws['!cols'] = [
      { wch: 8 },  // Ïó∞Î≤à
      { wch: 15 }, // ÎåÄÏÉÅÏûêÎ™Ö
      { wch: 15 }, // ÎåÄÏÉÅÍµ¨Î∂Ñ
      { wch: 20 }, // ÎåÄÏÉÅÍ∞ÄÍµ¨
      { wch: 12 }, // Ïö∞Ìé∏Î≤àÌò∏
      { wch: 25 }, // Í∏∞Î≥∏Ï£ºÏÜå
      { wch: 20 }, // ÏÉÅÏÑ∏Ï£ºÏÜå
      { wch: 15 }, // Ìï∏ÎìúÌè∞
      { wch: 15 }, // ÏßëÏ†ÑÌôî
      { wch: 50 }, // Ïã†Ï≤≠ÏÇ¨Ïú† (ÎÑìÍ≤å)
      { wch: 30 }  // Ï∞æÏïÑÍ∞ÄÎäîÍ∏∏
    ];

    // Ìñâ ÎÜíÏù¥ ÏÑ§Ï†ï Ï†úÍ±∞ - Í∏∞Î≥∏ ÌñâÎÜíÏù¥ ÏÇ¨Ïö©

    // ÏõåÌÅ¨ÏãúÌä∏Î•º ÏõåÌÅ¨Î∂ÅÏóê Ï∂îÍ∞Ä
    XLSX.utils.book_append_sheet(wb, ws, 'ÎåÄÏÉÅÏûêÎ™ÖÎã®');

    // ÏóëÏÖÄ ÌååÏùº Îã§Ïö¥Î°úÎìú
    const fileName = `${group?.name || 'ÎåÄÏÉÅÏûê'}_Î™ÖÎã®_${new Date().toISOString().split('T')[0]}.xlsx`;
    XLSX.writeFile(wb, fileName);
  };

  return (
    <TargetListContainer>
      <Header>
        <HeaderTop>
          <Title>ÎåÄÏÉÅÏûê Í¥ÄÎ¶¨</Title>
          <CloseButton onClick={onClose}>√ó</CloseButton>
        </HeaderTop>
        <GroupInfo>
          {group.name} - Ï¥ù {targets.length}Î™Ö
        </GroupInfo>
      </Header>

      <ActionBar>
        <SearchInput
          type="text"
          placeholder="ÎåÄÏÉÅÏûêÎ™Ö, Ï†ÑÌôîÎ≤àÌò∏, ÎåÄÏÉÅÍµ¨Î∂Ñ, ÎåÄÏÉÅÍ∞ÄÍµ¨Î°ú Í≤ÄÏÉâ..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <ButtonGroup>
          <ExcelButton onClick={handleExcelDownload}>
            üìä ÏóëÏÖÄÎ∞õÍ∏∞
          </ExcelButton>
          <AddButton onClick={handleAdd}>
            + ÎåÄÏÉÅÏûê Ï∂îÍ∞Ä
          </AddButton>
        </ButtonGroup>
      </ActionBar>

      <ListContainer>
        {filteredTargets.length === 0 ? (
          <EmptyState>
            <EmptyIcon>üë•</EmptyIcon>
            <EmptyText>
              {searchTerm ? 'Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§.' : 'Îì±Î°ùÎêú ÎåÄÏÉÅÏûêÍ∞Ä ÏóÜÏäµÎãàÎã§.'}
            </EmptyText>
          </EmptyState>
        ) : (
          filteredTargets.map((target) => (
            <TargetItem key={target.id}>
              <TargetHeader>
                <TargetName>{target.name}</TargetName>
                <TargetStatus status={target.status}>
                  {target.status === 'active' ? 'ÌôúÏÑ±' : 'ÎπÑÌôúÏÑ±'}
                </TargetStatus>
              </TargetHeader>

              <TargetDetails>
                {target.targetType && <div>üè∑Ô∏è ÎåÄÏÉÅÍµ¨Î∂Ñ: {target.targetType}</div>}
                {target.targetHousehold && <div>üè† ÎåÄÏÉÅÍ∞ÄÍµ¨: {target.targetHousehold}</div>}
                {target.mobilePhone && <div>üì± Ìï∏ÎìúÌè∞: {formatPhoneNumber(target.mobilePhone)}</div>}
                {target.phone && <div>‚òéÔ∏è ÏßëÏ†ÑÌôî: {formatPhoneNumber(target.phone)}</div>}
                <div>üìç {target.address}</div>
                {target.detailAddress && <div>üìç ÏÉÅÏÑ∏: {target.detailAddress}</div>}
                {target.applicationReason && <div>üìù Ïã†Ï≤≠ÏÇ¨Ïú†: {target.applicationReason.substring(0, 50)}{target.applicationReason.length > 50 ? '...' : ''}</div>}
                {target.directions && <div>üó∫Ô∏è Ï∞æÏïÑÍ∞ÄÎäîÍ∏∏: {target.directions}</div>}
                <div>üìÖ Îì±Î°ùÏùº: {target.registeredAt}</div>
              </TargetDetails>

              <TargetActions>
                <ActionButton 
                  className="edit"
                  onClick={() => handleEdit(target)}
                >
                  ÏàòÏ†ï
                </ActionButton>
                <ActionButton 
                  className="delete"
                  onClick={() => handleDelete(target)}
                >
                  ÏÇ≠Ï†ú
                </ActionButton>
              </TargetActions>
            </TargetItem>
          ))
        )}
      </ListContainer>

      {isModalOpen && (
        <TargetModal
          target={selectedTarget}
          onSave={handleSave}
          onClose={() => {
            setIsModalOpen(false);
            setSelectedTarget(null);
          }}
        />
      )}

      {isDeleteModalOpen && (
        <ConfirmModal
          title="ÎåÄÏÉÅÏûê ÏÇ≠Ï†ú"
          message={`${targetToDelete?.name}ÎãòÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?`}
          onConfirm={handleConfirmDelete}
          onCancel={() => {
            setIsDeleteModalOpen(false);
            setTargetToDelete(null);
          }}
        />
      )}
    </TargetListContainer>
  );
};

export default TargetList;
