import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  border-radius: 16px;
  padding: 2rem;
  width: 90%;
  max-width: 500px;
  max-height: 90vh;
  overflow-y: auto;
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1.5rem;
`;

const ModalTitle = styled.h3`
  font-size: 1.5rem;
  font-weight: 600;
  color: #1e293b;
`;

const CloseButton = styled.button`
  padding: 0.5rem;
  background-color: #f3f4f6;
  color: #6b7280;
  border-radius: 8px;
  font-size: 1.2rem;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #e5e7eb;
  }
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  font-size: 0.9rem;
  font-weight: 500;
  color: #374151;
  margin-bottom: 0.5rem;
`;

const Input = styled.input`
  padding: 0.75rem;
  border: 2px solid #e5e7eb;
  border-radius: 8px;
  font-size: 1rem;
  transition: border-color 0.2s ease;

  &:focus {
    border-color: #667eea;
  }
`;

const Select = styled.select`
  padding: 0.75rem;
  border: 2px solid #e5e7eb;
  border-radius: 8px;
  font-size: 1rem;
  background-color: white;
  transition: border-color 0.2s ease;

  &:focus {
    border-color: #667eea;
  }
`;

const TextArea = styled.textarea`
  padding: 0.75rem;
  border: 2px solid #e5e7eb;
  border-radius: 8px;
  font-size: 1rem;
  min-height: 80px;
  resize: vertical;
  transition: border-color 0.2s ease;

  &:focus {
    border-color: #667eea;
  }
`;

const AddressGroup = styled.div`
  display: flex;
  gap: 0.5rem;
  align-items: flex-end;
`;

const AddressInput = styled.input`
  padding: 0.75rem;
  border: 2px solid #e5e7eb;
  border-radius: 8px;
  font-size: 1rem;
  transition: border-color 0.2s ease;

  &:focus {
    border-color: #667eea;
  }

  &.zipcode {
    width: 120px;
  }

  &.address {
    flex: 1;
  }
`;

const AddressButton = styled.button`
  padding: 0.75rem 1rem;
  background-color: #667eea;
  color: white;
  border-radius: 8px;
  font-size: 0.9rem;
  font-weight: 500;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #5a67d8;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1.5rem;
`;

const Button = styled.button`
  flex: 1;
  padding: 0.75rem;
  border-radius: 8px;
  font-size: 1rem;
  font-weight: 500;
  transition: all 0.2s ease;

  &.save {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;

    &:hover {
      transform: translateY(-1px);
    }
  }

  &.cancel {
    background-color: #f3f4f6;
    color: #6b7280;

    &:hover {
      background-color: #e5e7eb;
    }
  }
`;

const TargetModal = ({ target, onSave, onClose }) => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    zipcode: '',
    address: '',
    detailAddress: '',
    status: 'active',
    notes: ''
  });

  useEffect(() => {
    if (target) {
      // 기존 주소를 분리 (예: "12345 서울시 강남구 테헤란로 123")
      const addressParts = target.address ? target.address.split(' ') : ['', '', ''];
      setFormData({
        name: target.name || '',
        phone: target.phone || '',
        zipcode: addressParts[0] || '',
        address: addressParts.slice(1).join(' ') || '',
        detailAddress: target.detailAddress || '',
        status: target.status || 'active',
        notes: target.notes || ''
      });
    } else {
      setFormData({
        name: '',
        phone: '',
        zipcode: '',
        address: '',
        detailAddress: '',
        status: 'active',
        notes: ''
      });
    }
  }, [target]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddressSearch = () => {
    // 다음 주소검색 API 스크립트 로드
    if (!window.daum) {
      const script = document.createElement('script');
      script.src = '//t1.daumcdn.net/mapjsapi/bundle/postcode/prod/postcode.v2.js';
      script.onload = () => {
        openAddressSearch();
      };
      document.head.appendChild(script);
    } else {
      openAddressSearch();
    }
  };

  const openAddressSearch = () => {
    new window.daum.Postcode({
      oncomplete: function(data) {
        // 우편번호와 주소 정보를 해당 필드에 넣는다.
        setFormData(prev => ({
          ...prev,
          zipcode: data.zonecode,
          address: data.address
        }));
      }
    }).open();
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!formData.name.trim() || !formData.phone.trim()) {
      alert('이름과 전화번호는 필수 입력 항목입니다.');
      return;
    }

    // 주소 정보를 합쳐서 저장
    const fullAddress = `${formData.zipcode} ${formData.address}`.trim();
    const submitData = {
      ...formData,
      address: fullAddress
    };

    onSave(submitData);
  };

  return (
    <ModalOverlay onClick={onClose}>
      <ModalContent onClick={(e) => e.stopPropagation()}>
        <ModalHeader>
          <ModalTitle>
            {target ? '대상자 수정' : '대상자 추가'}
          </ModalTitle>
          <CloseButton onClick={onClose}>×</CloseButton>
        </ModalHeader>

        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label htmlFor="name">이름 *</Label>
            <Input
              id="name"
              name="name"
              type="text"
              value={formData.name}
              onChange={handleChange}
              placeholder="이름을 입력하세요"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="phone">전화번호 *</Label>
            <Input
              id="phone"
              name="phone"
              type="tel"
              value={formData.phone}
              onChange={handleChange}
              placeholder="010-1234-5678"
              required
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="zipcode">주소</Label>
            <AddressGroup>
              <AddressInput
                id="zipcode"
                name="zipcode"
                type="text"
                className="zipcode"
                value={formData.zipcode}
                onChange={handleChange}
                placeholder="우편번호"
                readOnly
              />
              <AddressButton type="button" onClick={handleAddressSearch}>
                주소검색
              </AddressButton>
            </AddressGroup>
            <AddressInput
              name="address"
              type="text"
              className="address"
              value={formData.address}
              onChange={handleChange}
              placeholder="기본주소"
              readOnly
              style={{ marginTop: '0.5rem' }}
            />
            <Input
              name="detailAddress"
              type="text"
              value={formData.detailAddress}
              onChange={handleChange}
              placeholder="상세주소를 입력하세요"
              style={{ marginTop: '0.5rem' }}
            />
          </FormGroup>

          <FormGroup>
            <Label htmlFor="status">상태</Label>
            <Select
              id="status"
              name="status"
              value={formData.status}
              onChange={handleChange}
            >
              <option value="active">활성</option>
              <option value="inactive">비활성</option>
            </Select>
          </FormGroup>

          <FormGroup>
            <Label htmlFor="notes">메모</Label>
            <TextArea
              id="notes"
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              placeholder="추가 메모를 입력하세요"
            />
          </FormGroup>

          <ButtonGroup>
            <Button type="button" className="cancel" onClick={onClose}>
              취소
            </Button>
            <Button type="submit" className="save">
              {target ? '수정' : '추가'}
            </Button>
          </ButtonGroup>
        </Form>
      </ModalContent>
    </ModalOverlay>
  );
};

export default TargetModal;
