  const handleAddGroup = () => {
    setEditingGroup(null);
    setIsGroupModalOpen(true);
  };

  const handleEditGroup = (group) => {
    setEditingGroup(group);
    setIsGroupModalOpen(true);
  };

  const handleDeleteGroup = (group) => {
    setGroupToDelete(group);
    setIsDeleteModalOpen(true);
  };

  const handleSaveGroup = (groupData) => {
    let updatedGroups;
    
    if (editingGroup) {
      // 수정
      updatedGroups = groups.map(group =>
        group.id === editingGroup.id ? { ...group, ...groupData } : group
      );
    } else {
      // 추가
      const newGroup = {
        id: Date.now(),
        ...groupData,
        targetCount: 0,
        createdAt: new Date().toISOString().split('T')[0]
      };
      updatedGroups = [...groups, newGroup];
    }
    
    setGroups(updatedGroups);
    setIsGroupModalOpen(false);
    setEditingGroup(null);
  };

  const handleConfirmDeleteGroup = () => {
    const updatedGroups = groups.filter(group => group.id !== groupToDelete.id);
    setGroups(updatedGroups);
    setIsDeleteModalOpen(false);
    setGroupToDelete(null);
    
    // 선택된 그룹이 삭제된 경우 선택 해제
    if (selectedGroup && selectedGroup.id === groupToDelete.id) {
      setSelectedGroup(null);
      setIsSplit(false);
      setTargets([]);
    }
  };

  return (
    <DashboardContainer>
      <Header>
        <Logo>나눔번들</Logo>
        <UserInfo>
          <BusinessName>{user.businessName}</BusinessName>
          <LogoutButton onClick={onLogout}>
            로그아웃
          </LogoutButton>
        </UserInfo>
      </Header>

      <MainContent>
        <LeftPanel isSplit={isSplit}>
          <AddGroupButton onClick={handleAddGroup}>
            + 그룹 추가
          </AddGroupButton>
          <GroupGrid 
            groups={groups}
            onGroupSelect={handleGroupSelect}
            selectedGroup={selectedGroup}
            onEditGroup={handleEditGroup}
            onDeleteGroup={handleDeleteGroup}
          />
        </LeftPanel>

        <RightPanel isSplit={isSplit}>
          {isSplit && selectedGroup && (
            <TargetList
              group={selectedGroup}
              targets={targets}
              onClose={handleCloseSplit}
              onTargetUpdate={handleTargetUpdate}
            />
          )}
        </RightPanel>
      </MainContent>

      {isGroupModalOpen && (
        <GroupModal
          group={editingGroup}
          onSave={handleSaveGroup}
          onClose={() => {
            setIsGroupModalOpen(false);
            setEditingGroup(null);
          }}
        />
      )}

      {isDeleteModalOpen && (
        <ConfirmModal
          title="그룹 삭제"
          message={`${groupToDelete?.name} 그룹을 삭제하시겠습니까?`}
          onConfirm={handleConfirmDeleteGroup}
          onCancel={() => {
            setIsDeleteModalOpen(false);
            setGroupToDelete(null);
          }}
        />
      )}
    </DashboardContainer>
  );
};

export default Dashboard;
