import React, { useState } from 'react';
import styled from 'styled-components';
import TargetModal from './TargetModal';
import ConfirmModal from './ConfirmModal';
import * as XLSX from 'xlsx';

const TargetListContainer = styled.div`
  height: 100%;
  display: flex;
  flex-direction: column;
`;

const Header = styled.div`
  padding: 1.5rem;
  border-bottom: 1px solid #e5e7eb;
  background: white;
`;

const HeaderTop = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
`;

const Title = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #1e293b;
`;

const CloseButton = styled.button`
  padding: 0.5rem;
  background-color: #f3f4f6;
  color: #6b7280;
  border-radius: 8px;
  font-size: 1.2rem;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #e5e7eb;
  }
`;

const GroupInfo = styled.div`
  color: #64748b;
  font-size: 0.95rem;
`;

const ActionBar = styled.div`
  padding: 1rem 1.5rem;
  border-bottom: 1px solid #e5e7eb;
  background: #f8fafc;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const SearchInput = styled.input`
  padding: 0.75rem 1rem;
  border: 1px solid #d1d5db;
  border-radius: 8px;
  font-size: 0.9rem;
  width: 250px;

  &:focus {
    border-color: #667eea;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 0.75rem;
`;

const AddButton = styled.button`
  padding: 0.75rem 1.5rem;
  background: linear-gradient(135deg, #10b981 0%, #059669 100%);
  color: white;
  border-radius: 8px;
  font-size: 0.9rem;
  font-weight: 500;
  transition: transform 0.2s ease;

  &:hover {
    transform: translateY(-1px);
  }
`;

const ExcelButton = styled.button`
  padding: 0.75rem 1.5rem;
  background: linear-gradient(135deg, #059669 0%, #047857 100%);
  color: white;
  border-radius: 8px;
  font-size: 0.9rem;
  font-weight: 500;
  transition: transform 0.2s ease;
  white-space: nowrap;

  &:hover {
    transform: translateY(-1px);
  }
`;

const ListContainer = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 1rem;
`;

const TargetItem = styled.div`
  background: white;
  border: 1px solid #e5e7eb;
  border-radius: 12px;
  padding: 1rem;
  margin-bottom: 0.75rem;
  transition: all 0.2s ease;

  &:hover {
    border-color: #667eea;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
  }
`;

const TargetHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 0.5rem;
`;

const TargetName = styled.h4`
  font-size: 1.1rem;
  font-weight: 600;
  color: #1e293b;
`;

const TargetStatus = styled.span`
  padding: 0.25rem 0.5rem;
  border-radius: 12px;
  font-size: 0.75rem;
  font-weight: 500;
  background-color: ${props => props.status === 'active' ? '#dcfce7' : '#fef3c7'};
  color: ${props => props.status === 'active' ? '#166534' : '#92400e'};
`;

const TargetDetails = styled.div`
  color: #64748b;
  font-size: 0.9rem;
  margin-bottom: 0.75rem;
`;

const TargetActions = styled.div`
  display: flex;
  gap: 0.5rem;
`;

const ActionButton = styled.button`
  padding: 0.5rem 1rem;
  border-radius: 6px;
  font-size: 0.8rem;
  font-weight: 500;
  transition: all 0.2s ease;

  &.edit {
    background-color: #3b82f6;
    color: white;

    &:hover {
      background-color: #2563eb;
    }
  }

  &.delete {
    background-color: #ef4444;
    color: white;

    &:hover {
      background-color: #dc2626;
    }
  }
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 3rem;
  color: #64748b;
`;

const EmptyIcon = styled.div`
  font-size: 2.5rem;
  margin-bottom: 1rem;
`;

const EmptyText = styled.p`
  font-size: 1rem;
`;

const TargetList = ({ group, targets, onClose, onTargetUpdate }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedTarget, setSelectedTarget] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [targetToDelete, setTargetToDelete] = useState(null);

  const filteredTargets = targets.filter(target =>
    target.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    (target.mobilePhone && target.mobilePhone.includes(searchTerm)) ||
    (target.homePhone && target.homePhone.includes(searchTerm)) ||
    (target.targetType && target.targetType.includes(searchTerm)) ||
    (target.targetHousehold && target.targetHousehold.includes(searchTerm))
  );

  const handleEdit = (target) => {
    setSelectedTarget(target);
    setIsModalOpen(true);
  };

  const handleDelete = (target) => {
    setTargetToDelete(target);
    setIsDeleteModalOpen(true);
  };

  const handleAdd = () => {
    setSelectedTarget(null);
    setIsModalOpen(true);
  };

  const handleSave = (targetData) => {
    let updatedTargets;
    
    if (selectedTarget) {
      // 수정
      updatedTargets = targets.map(target =>
        target.id === selectedTarget.id ? { ...target, ...targetData } : target
      );
    } else {
      // 추가
      const newTarget = {
        id: Date.now(),
        ...targetData,
        registeredAt: new Date().toISOString().split('T')[0]
      };
      updatedTargets = [...targets, newTarget];
    }
    
    onTargetUpdate(updatedTargets);
    setIsModalOpen(false);
    setSelectedTarget(null);
  };

  const handleConfirmDelete = () => {
    const updatedTargets = targets.filter(target => target.id !== targetToDelete.id);
    onTargetUpdate(updatedTargets);
    setIsDeleteModalOpen(false);
    setTargetToDelete(null);
  };

  const handleExcelDownload = () => {
    // 엑셀 워크북 생성
    const wb = XLSX.utils.book_new();
    
    // 워크시트 데이터 생성
    const wsData = [];
    
    // 제목 행
    wsData.push(['2025년 아름다운 나눔보따리 대상자 명단']);
    wsData.push([]);
    
    // 1. 단체정보 섹션
    wsData.push(['1. 단체정보']);
    wsData.push(['단체명', '', '', '', '', '', '담당자명']);
    wsData.push(['주소', '', '', '', '', '', '핸드폰/전화번호']);
    wsData.push([]);
    
    // 2. 추천대상 정보 섹션
    wsData.push(['2. 추천대상 정보']);
    wsData.push([]);
    wsData.push(['', '', '', '추천 대상자 명단', '', '', '', '', '', '']);
    wsData.push(['연번', '대상자명', '대상구분', '대상가구', '주소(정확하게 기재 필수)', '', '', '연락처(정확히 기재)', '신청사유', '찾아가는길']);
    wsData.push(['', '', '(수급자/차상위/일반저소득)', '(독거어르신/조손가구/저소득 1인가구/겨울철 에너지 취약계층 등)', '우편번호', '도로명 주소(정확히 기재)', '상세주소(건물명, 동/호)', '(집전화/핸드폰)', '(대상자 경제적, 환경적 상황 등 간략히 작성)', '(방문 어려운 위치의 가구일 경우 선택작성)']);
    
    // 대상자 데이터 추가
    targets.forEach((target, index) => {
      const addressParts = target.address ? target.address.split(' ') : ['', ''];
      const zipcode = addressParts[0] || '';
      const roadAddress = addressParts.slice(1).join(' ') || '';
      
      wsData.push([
        index + 1,
        target.name || '',
        target.targetType || '',
        target.targetHousehold || '',
        zipcode,
        roadAddress,
        target.detailAddress || '',
        `${target.homePhone || ''}/${target.mobilePhone || ''}`,
        target.applicationReason || '',
        target.directions || ''
      ]);
    });
    
    // 워크시트 생성
    const ws = XLSX.utils.aoa_to_sheet(wsData);
    
    // 셀 병합 설정
    const merges = [
      // 제목 병합 (A1:J1)
      { s: { r: 0, c: 0 }, e: { r: 0, c: 9 } },
      // 추천 대상자 명단 병합 (D3:J3)
      { s: { r: 2, c: 3 }, e: { r: 2, c: 9 } },
      // 대상구분 병합 (C4:C5)
      { s: { r: 3, c: 2 }, e: { r: 4, c: 2 } },
      // 대상가구 병합 (D4:D5)
      { s: { r: 3, c: 3 }, e: { r: 4, c: 3 } },
      // 주소 병합 (E4:G4)
      { s: { r: 3, c: 4 }, e: { r: 3, c: 6 } },
      // 연락처 병합 (H4:H5)
      { s: { r: 3, c: 7 }, e: { r: 4, c: 7 } },
      // 신청사유 병합 (I4:I5)
      { s: { r: 3, c: 8 }, e: { r: 4, c: 8 } },
      // 찾아가는길 병합 (J4:J5)
      { s: { r: 3, c: 9 }, e: { r: 4, c: 9 } }
    ];
    
    ws['!merges'] = merges;
    
    // 열 너비 설정
    ws['!cols'] = [
      { wch: 8 },  // 연번
      { wch: 15 }, // 대상자명
      { wch: 20 }, // 대상구분
      { wch: 25 }, // 대상가구
      { wch: 12 }, // 우편번호
      { wch: 30 }, // 도로명 주소
      { wch: 20 }, // 상세주소
      { wch: 20 }, // 연락처
      { wch: 40 }, // 신청사유
      { wch: 30 }  // 찾아가는길
    ];
    
    // 워크시트를 워크북에 추가
    XLSX.utils.book_append_sheet(wb, ws, '대상자명단');
    
    // 그룹 정보를 워크시트에 추가
    if (group) {
      // 단체명과 담당자명을 실제 그룹 정보로 업데이트
      wsData[2][0] = group.organizationName || '';
      wsData[2][6] = group.managerName || '';
      wsData[3][0] = group.address || '';
      wsData[3][6] = `${group.phone || ''}/${group.mobilePhone || ''}`;
    }
    
    // 엑셀 파일 다운로드
    const fileName = `${group?.name || '대상자'}_명단_${new Date().toISOString().split('T')[0]}.xlsx`;
    XLSX.writeFile(wb, fileName);
  };

  return (
    <TargetListContainer>
      <Header>
        <HeaderTop>
          <Title>대상자 관리</Title>
          <CloseButton onClick={onClose}>×</CloseButton>
        </HeaderTop>
        <GroupInfo>
          {group.name} - 총 {targets.length}명
        </GroupInfo>
      </Header>

      <ActionBar>
        <SearchInput
          type="text"
          placeholder="대상자명, 전화번호, 대상구분, 대상가구로 검색..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <ButtonGroup>
          <ExcelButton onClick={handleExcelDownload}>
            📊 엑셀받기
          </ExcelButton>
          <AddButton onClick={handleAdd}>
            + 대상자 추가
          </AddButton>
        </ButtonGroup>
      </ActionBar>

      <ListContainer>
        {filteredTargets.length === 0 ? (
          <EmptyState>
            <EmptyIcon>👥</EmptyIcon>
            <EmptyText>
              {searchTerm ? '검색 결과가 없습니다.' : '등록된 대상자가 없습니다.'}
            </EmptyText>
          </EmptyState>
        ) : (
          filteredTargets.map((target) => (
            <TargetItem key={target.id}>
              <TargetHeader>
                <TargetName>{target.name}</TargetName>
                <TargetStatus status={target.status}>
                  {target.status === 'active' ? '활성' : '비활성'}
                </TargetStatus>
              </TargetHeader>
              
              <TargetDetails>
                {target.targetType && <div>🏷️ 대상구분: {target.targetType}</div>}
                {target.targetHousehold && <div>🏠 대상가구: {target.targetHousehold}</div>}
                {target.mobilePhone && <div>📱 핸드폰: {target.mobilePhone}</div>}
                {target.homePhone && <div>☎️ 집전화: {target.homePhone}</div>}
                <div>📍 {target.address}</div>
                {target.detailAddress && <div>📍 상세: {target.detailAddress}</div>}
                {target.applicationReason && <div>📝 신청사유: {target.applicationReason.substring(0, 50)}{target.applicationReason.length > 50 ? '...' : ''}</div>}
                {target.directions && <div>🗺️ 찾아가는길: {target.directions}</div>}
                <div>📅 등록일: {target.registeredAt}</div>
              </TargetDetails>
              
              <TargetActions>
                <ActionButton 
                  className="edit"
                  onClick={() => handleEdit(target)}
                >
                  수정
                </ActionButton>
                <ActionButton 
                  className="delete"
                  onClick={() => handleDelete(target)}
                >
                  삭제
                </ActionButton>
              </TargetActions>
            </TargetItem>
          ))
        )}
      </ListContainer>

      {isModalOpen && (
        <TargetModal
          target={selectedTarget}
          onSave={handleSave}
          onClose={() => {
            setIsModalOpen(false);
            setSelectedTarget(null);
          }}
        />
      )}

      {isDeleteModalOpen && (
        <ConfirmModal
          title="대상자 삭제"
          message={`${targetToDelete?.name}님을 삭제하시겠습니까?`}
          onConfirm={handleConfirmDelete}
          onCancel={() => {
            setIsDeleteModalOpen(false);
            setTargetToDelete(null);
          }}
        />
      )}
    </TargetListContainer>
  );
};

export default TargetList;
