import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  border-radius: 16px;
  padding: 2rem;
  width: 90%;
  max-width: 500px;
  max-height: 90vh;
  overflow-y: auto;
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1.5rem;
`;

const ModalTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #1e293b;
`;

const CloseButton = styled.button`
  padding: 0.5rem;
  background-color: #f3f4f6;
  color: #6b7280;
  border-radius: 8px;
  font-size: 1.2rem;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #e5e7eb;
  }
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const Label = styled.label`
  font-size: 0.9rem;
  font-weight: 500;
  color: #374151;

  &.required {
    color: #dc2626;
  }
`;

const RequiredStar = styled.span`
  color: #dc2626;
  margin-left: 2px;
`;

const ErrorMessage = styled.div`
  color: #dc2626;
  font-size: 0.8rem;
  margin-top: 0.25rem;
`;

const MessageContainer = styled.div`
  position: fixed;
  top: 20px;
  right: 20px;
  z-index: 1001;
  max-width: 400px;
`;

const Message = styled.div`
  padding: 1rem;
  border-radius: 8px;
  margin-bottom: 0.5rem;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);

  &.success {
    background-color: #d1fae5;
    color: #065f46;
    border: 1px solid #a7f3d0;
  }

  &.error {
    background-color: #fee2e2;
    color: #991b1b;
    border: 1px solid #fca5a5;
  }

  &.info {
    background-color: #dbeafe;
    color: #1e40af;
    border: 1px solid #93c5fd;
  }
`;

const Input = styled.input`
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 8px;
  font-size: 0.9rem;
  transition: border-color 0.2s ease;

  &:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
  }

  &.error {
    border-color: #dc2626;
    box-shadow: 0 0 0 3px rgba(220, 38, 38, 0.1);
  }
`;

const TextArea = styled.textarea`
  padding: 0.75rem;
  border: 1px solid #d1d5db;
  border-radius: 8px;
  font-size: 0.9rem;
  min-height: 80px;
  resize: vertical;
  transition: border-color 0.2s ease;

  &:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
  }

  &.error {
    border-color: #dc2626;
    box-shadow: 0 0 0 3px rgba(220, 38, 38, 0.1);
  }
`;


const AddressGroup = styled.div`
  display: flex;
  gap: 0.5rem;
  align-items: flex-end;
`;

const AddressInput = styled(Input)`
  &.zipcode {
    width: 120px;
  }

  &.address {
    flex: 1;
  }
`;

const AddressButton = styled.button`
  padding: 0.75rem 1rem;
  background-color: #3b82f6;
  color: white;
  border-radius: 8px;
  font-size: 0.9rem;
  font-weight: 500;
  white-space: nowrap;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #2563eb;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
`;

const Button = styled.button`
  flex: 1;
  padding: 0.75rem 1rem;
  border-radius: 8px;
  font-size: 0.9rem;
  font-weight: 500;
  transition: all 0.2s ease;

  &.primary {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;

    &:hover {
      transform: translateY(-1px);
    }
  }

  &.secondary {
    background-color: #f3f4f6;
    color: #374151;

    &:hover {
      background-color: #e5e7eb;
    }
  }
`;

const GroupModal = ({ group, onSave, onClose }) => {
  const [formData, setFormData] = useState({
    name: '',
    organizationName: '',
    managerName: '',
    zipcode: '',
    address: '',
    detailAddress: '',
    mobilePhone: '',
    phone: '',
    description: ''
  });

  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [message, setMessage] = useState(null);

  useEffect(() => {
    if (group) {
      // 데이터베이스 컬럼명을 React 필드명으로 매핑
      setFormData({
        name: group.group_name || '',
        organizationName: group.org_name || '',
        managerName: group.contact_name || '',
        zipcode: group.zipcode || '',
        address: group.address1 || '',
        detailAddress: group.address2 || '',
        mobilePhone: formatPhoneNumber(group.mobile_phone || ''),
        phone: formatPhoneNumber(group.office_phone || ''),
        description: group.description || ''
      });
    } else {
      setFormData({
        name: '',
        organizationName: '',
        managerName: '',
        zipcode: '',
        address: '',
        detailAddress: '',
        mobilePhone: '',
        phone: '',
        description: ''
      });
    }
  }, [group]);

  const validateField = (name, value) => {
    switch (name) {
      case 'name':
        if (!value.trim()) return '그룹명을 입력해주세요.';
        if (value.length > 15) return '그룹명은 15자 이내로 입력해주세요.';
        return '';
      case 'organizationName':
        if (!value.trim()) return '기관명을 입력해주세요.';
        if (value.length > 25) return '기관명은 25자 이내로 입력해주세요.';
        return '';
      case 'managerName':
        if (!value.trim()) return '담당자명을 입력해주세요.';
        if (value.length > 20) return '담당자명은 20자 이내로 입력해주세요.';
        return '';
      case 'address':
        if (!value.trim()) return '주소를 입력해주세요.';
        return '';
      case 'mobilePhone':
        if (!value.trim()) return '핸드폰 번호를 입력해주세요.';
        return '';
      case 'phone':
        if (value && value.length > 20) return '일반전화는 20자 이내로 입력해주세요.';
        return '';
      case 'description':
        if (value && value.length > 500) return '설명은 500자 이내로 입력해주세요.';
        return '';
      default:
        return '';
    }
  };

  // 전화번호 포맷팅 함수
  const formatPhoneNumber = (phone) => {
    if (!phone) return '';

    // 숫자만 추출
    const numbers = phone.replace(/\D/g, '');

    // 핸드폰 번호 (010, 011, 016, 017, 018, 019)
    if (numbers.length === 11 && numbers.startsWith('01')) {
      return `${numbers.slice(0, 3)}-${numbers.slice(3, 7)}-${numbers.slice(7)}`;
    }

    // 일반전화 번호 (02, 031, 032, 033, 041, 042, 043, 044, 051, 052, 053, 054, 055, 061, 062, 063, 064)
    if (numbers.length === 10) {
      if (numbers.startsWith('02')) {
        return `${numbers.slice(0, 2)}-${numbers.slice(2, 6)}-${numbers.slice(6)}`;
      } else {
        return `${numbers.slice(0, 3)}-${numbers.slice(3, 6)}-${numbers.slice(6)}`;
      }
    }

    if (numbers.length === 9) {
      if (numbers.startsWith('02')) {
        return `${numbers.slice(0, 2)}-${numbers.slice(2, 5)}-${numbers.slice(5)}`;
      } else {
        return `${numbers.slice(0, 3)}-${numbers.slice(3, 5)}-${numbers.slice(5)}`;
      }
    }

    // 포맷팅이 안되는 경우 원본 반환
    return phone;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    // 전화번호 필드인 경우 포맷팅 적용
    let formattedValue = value;
    if (name === 'mobilePhone' || name === 'phone') {
      formattedValue = formatPhoneNumber(value);
    }

    setFormData(prev => ({
      ...prev,
      [name]: formattedValue
    }));

    // 실시간 검증 (원본 값으로 검증)
    const errorMessage = validateField(name, value);
    setErrors(prev => ({
      ...prev,
      [name]: errorMessage
    }));
  };

  const handleAddressSearch = () => {
    // 다음 우편번호 서비스 사용
    new window.daum.Postcode({
      oncomplete: function(data) {
        // 우편번호와 주소 정보를 해당 필드에 넣는다.
        const newAddress = data.address;
        setFormData(prev => ({
          ...prev,
          zipcode: data.zonecode,
          address: newAddress
        }));

        // 주소 실시간 검증
        const errorMessage = validateField('address', newAddress);
        setErrors(prev => ({
          ...prev,
          address: errorMessage
        }));
      }
    }).open();
  };

  const validateForm = () => {
    const newErrors = {};

    // 필수 필드 검증 (5개 항목)
    if (!formData.name.trim()) {
      newErrors.name = '그룹명을 입력해주세요.';
    } else if (formData.name.length > 15) {
      newErrors.name = '그룹명은 15자 이내로 입력해주세요.';
    }

    if (!formData.organizationName.trim()) {
      newErrors.organizationName = '기관명을 입력해주세요.';
    } else if (formData.organizationName.length > 25) {
      newErrors.organizationName = '기관명은 25자 이내로 입력해주세요.';
    }

    if (!formData.managerName.trim()) {
      newErrors.managerName = '담당자명을 입력해주세요.';
    } else if (formData.managerName.length > 20) {
      newErrors.managerName = '담당자명은 20자 이내로 입력해주세요.';
    }

    if (!formData.address.trim()) {
      newErrors.address = '주소를 입력해주세요.';
    }

    if (!formData.mobilePhone.trim()) {
      newErrors.mobilePhone = '핸드폰 번호를 입력해주세요.';
    }

    // 선택 항목 검증 (입력 제한만)
    if (formData.phone && formData.phone.length > 20) {
      newErrors.phone = '일반전화는 20자 이내로 입력해주세요.';
    }

    if (formData.description && formData.description.length > 500) {
      newErrors.description = '설명은 500자 이내로 입력해주세요.';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const showMessage = (type, text) => {
    setMessage({ type, text });
    setTimeout(() => setMessage(null), 5000);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (isSubmitting) return;

    if (!validateForm()) {
      showMessage('error', '입력 정보를 확인해주세요.');
      return;
    }

    setIsSubmitting(true);

    try {
      // 주소 정보를 합쳐서 저장
      const fullAddress = `${formData.zipcode} ${formData.address}`.trim();
      const submitData = {
        ...formData,
        address: fullAddress
      };

      await onSave(submitData);
      showMessage('success', group ? '그룹이 수정되었습니다.' : '그룹이 추가되었습니다.');
    } catch (error) {
      console.error('저장 중 오류가 발생했습니다:', error);
      showMessage('error', '저장 중 오류가 발생했습니다. 다시 시도해주세요.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      {message && (
        <MessageContainer>
          <Message className={message.type}>
            {message.text}
          </Message>
        </MessageContainer>
      )}
      <ModalOverlay onClick={onClose}>
        <ModalContent onClick={(e) => e.stopPropagation()}>
        <ModalHeader>
          <ModalTitle>
            {group ? '그룹 수정' : '그룹 추가'}
          </ModalTitle>
          <CloseButton onClick={onClose}>×</CloseButton>
        </ModalHeader>

        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label htmlFor="name" className="required">
              그룹명 <RequiredStar>*</RequiredStar>
            </Label>
            <Input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="그룹명을 입력하세요"
              className={errors.name ? 'error' : ''}
            />
            {errors.name && <ErrorMessage>{errors.name}</ErrorMessage>}
          </FormGroup>

          <FormGroup>
            <Label htmlFor="organizationName" className="required">
              기관명 <RequiredStar>*</RequiredStar>
            </Label>
            <Input
              type="text"
              id="organizationName"
              name="organizationName"
              value={formData.organizationName}
              onChange={handleChange}
              placeholder="기관명을 입력하세요"
              className={errors.organizationName ? 'error' : ''}
            />
            {errors.organizationName && <ErrorMessage>{errors.organizationName}</ErrorMessage>}
          </FormGroup>

          <FormGroup>
            <Label htmlFor="managerName" className="required">
              담당자명 <RequiredStar>*</RequiredStar>
            </Label>
            <Input
              type="text"
              id="managerName"
              name="managerName"
              value={formData.managerName}
              onChange={handleChange}
              placeholder="담당자명을 입력하세요"
              className={errors.managerName ? 'error' : ''}
            />
            {errors.managerName && <ErrorMessage>{errors.managerName}</ErrorMessage>}
          </FormGroup>

          <FormGroup>
            <Label htmlFor="zipcode" className="required">
              주소 <RequiredStar>*</RequiredStar>
            </Label>
            <AddressGroup>
              <AddressInput
                type="text"
                id="zipcode"
                name="zipcode"
                className={`zipcode ${errors.address ? 'error' : ''}`}
                value={formData.zipcode}
                onChange={handleChange}
                placeholder="우편번호"
                readOnly
              />
              <AddressButton type="button" onClick={handleAddressSearch}>
                주소검색
              </AddressButton>
            </AddressGroup>
            <AddressInput
              type="text"
              name="address"
              className={`address ${errors.address ? 'error' : ''}`}
              value={formData.address}
              onChange={handleChange}
              placeholder="기본주소"
              readOnly
              style={{ marginTop: '0.5rem' }}
            />
            <Input
              type="text"
              name="detailAddress"
              value={formData.detailAddress}
              onChange={handleChange}
              placeholder="상세주소를 입력하세요"
              style={{ marginTop: '0.5rem' }}
            />
            {errors.address && <ErrorMessage>{errors.address}</ErrorMessage>}
          </FormGroup>

          <FormGroup>
            <Label htmlFor="mobilePhone" className="required">
              핸드폰 <RequiredStar>*</RequiredStar>
            </Label>
            <Input
              type="tel"
              id="mobilePhone"
              name="mobilePhone"
              value={formData.mobilePhone}
              onChange={handleChange}
              placeholder="010-1234-5678"
              className={errors.mobilePhone ? 'error' : ''}
            />
            {errors.mobilePhone && <ErrorMessage>{errors.mobilePhone}</ErrorMessage>}
          </FormGroup>

          <FormGroup>
            <Label htmlFor="phone">
              일반전화
            </Label>
            <Input
              type="tel"
              id="phone"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              placeholder="02-1234-5678"
              className={errors.phone ? 'error' : ''}
            />
            {errors.phone && <ErrorMessage>{errors.phone}</ErrorMessage>}
          </FormGroup>

          <FormGroup>
            <Label htmlFor="description">
              설명
            </Label>
            <TextArea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              placeholder="그룹에 대한 설명을 입력하세요"
              className={errors.description ? 'error' : ''}
            />
            {errors.description && <ErrorMessage>{errors.description}</ErrorMessage>}
          </FormGroup>

          <ButtonGroup>
            <Button type="button" className="secondary" onClick={onClose} disabled={isSubmitting}>
              취소
            </Button>
            <Button type="submit" className="primary" disabled={isSubmitting}>
              {isSubmitting ? '처리중...' : (group ? '수정' : '추가')}
            </Button>
          </ButtonGroup>
        </Form>
      </ModalContent>
    </ModalOverlay>
    </>
  );
};

export default GroupModal;
